import React,{useState, useEffect} from "react";
import {
  ListGroup,
  ListGroupItem,
  ListGroupItemHeading,
  ListGroupItemText,
  Button,
  Form,
  FormGroup,
  Label,
  Input
} from "reactstrap";
import "../App.css";
import axios from 'axios';

const Comment= (props) =>(
    <ListGroupItem >
    <ListGroupItemHeading>{props.el.title}</ListGroupItemHeading>
    <ListGroupItemText>{props.el.date}</ListGroupItemText>
    <ListGroupItemText>{props.el.body}</ListGroupItemText>
  </ListGroupItem>
)

export default function Comments({id}) {
    const [comment, setComment]=useState([])

    const commentList= () =>{
        return comment.map(el =>{
           return  <Comment item={el} />
        })
    }


    //RETRIEVE COMMENTS FROM DATABASE
    useEffect(() => {
        axios


    },[])



  return (
    <div className="CommentContainer">
      <h4> Comments </h4>

      <ListGroup>

        {commentList()}

        <ListGroupItem >
          <ListGroupItemHeading>List group item heading</ListGroupItemHeading>
          <ListGroupItemText>20/05/2005</ListGroupItemText>
          <ListGroupItemText>
            Donec id elit non mi porta gravida at eget metus. Maecenas sed diam
            eget risus varius blandit.
          </ListGroupItemText>
        </ListGroupItem>



      </ListGroup>

      <Form className='CommentForm'>
      <p>Please leave a comment: </p>
      <FormGroup>
        <Label for="exampleEmail">Email</Label>
        <Input type="email" name="email" id="exampleEmail" placeholder="with a placeholder" />
      </FormGroup>
      <FormGroup>
        <Label for="examplePassword">Password</Label>
        <Input type="password" name="password" id="examplePassword" placeholder="password placeholder" />
      </FormGroup>
      <FormGroup>
        <Label for="exampleText">Text Area</Label>
        <Input type="textarea" name="text" id="exampleText" />
      </FormGroup>
      <Button>Submit</Button>
    </Form>
    </div>
  );
}
