import React, { useState, useEffect } from "react";
import axios from 'axios';
import { Link } from "react-router-dom";
import Moment from 'react-moment';
import "../App.css";
import {
  Container,
  Row,
  Col,
  Card,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
} from "reactstrap";

const Post = (props) => (

  <Col xs="12" md="4">
  <Link to={'/blogs/'+props.post._id}>
    <Card className="Card">
      <CardBody>
        <CardSubtitle><Moment date={props.post.date}/></CardSubtitle>
        <CardTitle className="CardTitle">{
          props.post.tags.map(tag =>{
            return <span>{tag}  </span>
          })
          }</CardTitle>
        <CardText className="CardText">
          {props.post.title} 
        </CardText>
      </CardBody>
    </Card>
    </Link>
  </Col>
);



function Homepage() {
  /// inital STATe
  const [posts, setPosts] = useState([]);
  console.log(posts)

  useEffect(() => {
    axios
      .get("http://localhost:8080/api/blogs")
      .then(function (res) {
        const data= res.data;
       setPosts(data);
        //console.log(data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  const postList =()=>{
    return posts.map(post => {
      return <Post post={post} />
    })
  }


  return (
    <Container>

      <Row>
       {postList()}
      </Row>
    </Container>
  );
}

export default Homepage;
